import React, { Component } from "react";
import App from "./App";
import Result from "./Result";

class Button extends React.Component {

 state = {
     result : ''
 }

 buttonPressed = buttonValue => {
     this.setState({
         result : {result}
     });
 }


 buttonPressed =(e) => {
     this.props.buttonPressed(e.target.value );
 }
    
  render() {
    return (
      <div className="MainDiv">
        <div>
          <label className="Label"> CALCULATOR </label>
        </div> 
        <div>
          <Result result={this.state.result}/>
        </div>
        <div className="button">
            <button id="Sbtn"> Clear </button>
            <button id="Sbtn"> Backspace </button> <br></br> 
            <button id="btn" value="1" onClick={this.buttonPressed}> 1</button>
            <button id="btn" value="2" onClick={this.buttonPressed}> 2</button>
            <button id="btn" value="3" onClick={this.buttonPressed}> 3</button>
            <button id="btn" value="+" onClick={this.buttonPressed}> +</button> <br></br>
            <button id="btn" value="4" onClick={this.buttonPressed}> 4</button>
            <button id="btn" value="5" onClick={this.buttonPressed}> 5</button>
            <button id="btn" value="6" onClick={this.buttonPressed}> 6</button>
            <button id="btn" value="-" onClick={this.buttonPressed}> -</button> <br></br>
            <button id="btn" value="7" onClick={this.buttonPressed}> 7</button>
            <button id="btn" value="8" onClick={this.buttonPressed}> 8</button>
            <button id="btn" value="9" onClick={this.buttonPressed}> 9</button>
            <button id="btn" value="*" onClick={this.buttonPressed}> *</button> <br></br>
            <button id="btn" value="=" onClick={this.buttonPressed}> =</button>
            <button id="btn" value="0" onClick={this.buttonPressed}> 0</button>
            <button id="btn" value="." onClick={this.buttonPressed}> .</button>
            <button id="btn" value="/" onClick={this.buttonPressed}> /</button> <br></br>
        </div>
      </div>
    );
  }
  
}



export default Button;
